// header file which defines the minimum range of the primitive integer ad floating point types in C and C++// this is guaranteed by the language#ifndef __CMINLIMITS_H#define __CMINLIMITS_H// char#define CMIN_CHAR_MIN			(-127-1)#define CMIN_CHAR_MAX			127// unsigned char#define	CMIN_UCHAR_MAX			255U// int#define CMIN_INT_MIN			(-32767-1)#define CMIN_INT_MAX			  32767// unsigned int#define CMIN_UINT_MAX			65535U// long int#define CMIN_LONG_MIN			(-2147483647L-1)#define CMIN_LONG_MAX			  2147483647L// unsigned long int#define CMIN_ULONG_MAX			4294967295U// float#define CMIN_FLT_DIG			6#define CMIN_FLT_MIN_10_EXP		(-37)#define CMIN_FLT_MAX_10_EXP		 37#define CMIN_FLT_MAX			10e+37F#define CMIN_FLT_EPSILON		10e-05F#define CMIN_FLT_MIN			10e-37F// double#define CMIN_DBL_DIG			10#define CMIN_DBL_MIN_10_EXP		CMIN_FLT_MIN_10_EXP#define CMIN_DBL_MAX_10_EXP		CMIN_FLT_MAX_10_EXP#define CMIN_DBL_MAX			CMIN_FLT_MAX#define CMIN_DBL_EPSILON		CMIN_FLT_EPSILON#define CMIN_DBL_MIN			CMIN_FLT_MIN// long double// same as double#endif // __CMINLIMITS_H