#include "TSuperTreeConsensus.h"TSuperTreeConsensus::TSuperTreeConsensus (const TSourceTreesFile* sourceTreesFile) 	: TConsensusTreesFile (*sourceTreesFile) {  	fSourceLeafSet = fSourceTreesBlock->GetLeafSet (false, true); 	 	fConsensusLeafSet.SetFull (fSourceLeafSet.GetLeaves ());	fLeafMap.SetMapping (fSourceLeafSet, fConsensusLeafSet);	 	 	fTaxaBlock->PruneLeaves (fTaxaBlock->GetLeafSet () - fSourceLeafSet);	if (fLeafMap.IsMapping ())			fTaxaBlock->MapLeaves (fLeafMap);	fConsensusTree.StarTree (fConsensusLeafSet);}void TSuperTreeConsensus::nextTree (void){	fSourceTreesBlock->GetIthTree (fTreesDone + 1, fCurSourceTree, true);		if (fLeafMap.IsMapping ())			fCurSourceTree.MapLeaves (fLeafMap);	CHECK_ (fCurSourceTree.GetLeafSet ().IsSubset (fConsensusLeafSet));	CHECK_ (fCurSourceTree.GetWeight ());}