#include "TArrySelect.h"void TArrySelect::Copy (const TObject& obj){	TSelectContainer::Copy (obj);	const TArrySelect& arrySelect = (const TArrySelect&) obj;	fArry.Copy (arrySelect.fArry);}void TArrySelect::DeleteIthItem (const unsigned int i, const bool selected) { 	fArry.DeleteIthItem (IthSelection (i,selected)); 	fItemSet.SetEmpty ();};void TArrySelect::DetachIthItem (const unsigned int i, TObject*& item, const bool selected) { 	fArry.DetachIthItem (IthSelection (i, selected), item); 	fItemSet.SetEmpty ();}