#include "CPLog.h"// Log windowextern VTextWindowPtr Log;#ifdef Macintosh//------------------------------------------------------------------------------void CPLogWindow::Create (char *title){	ToolbarHeight = 0; //30;	VTextWindow::Create (title);   	(**Editor).txFont = fnFace;}#endif//------------------------------------------------------------------------------void CPLogWindow::SetBaseFont (){#ifdef Macintosh	#ifdef RADCON // 14 July 1998 For Joe Thorley		strcpy (fFace, "PAUPMonaco"); 	#else		strcpy (fFace, "COMPONENTMonaco");	#endif	fSize = 9;	fBold = FALSE;	fItalic = FALSE;	short fn;	#ifdef RADCON // 14 July 1998 For Joe Thorley		::GetFNum ("\pPAUPMonaco", &fn);	#else		::GetFNum ("\pCOMPONENTMonaco", &fn);	#endif   	fnFace = fn;#else	strcpy (fFace, "MS LineDraw");	fSize = 9;	fBold = FALSE;	fItalic = FALSE;	fnFace = FindFaceInFontMenu();	SetFont ();#endif   	SetFont ();}/*****************************************************************************//*				                                                             *//* 						     CPLogDoc                                        *//*                                                                           *//*****************************************************************************///------------------------------------------------------------------------------void CPLogDoc::New (){		VViewWindowPtr win = new CPLogWindow;	win->Create ("Log");	viewList.Add (win);	win->SetParent (this);	Log = (VTextWindowPtr) win;}