#include "TReadFile.h"#if PRAGMA_MARK_SUPPORTED#pragma mark -#endifconst TString& TReadFile::GetNextWord (void){		fCurWord.SetNull ();		while (IsWhiteSpace (fCurChar) && !IsEOF ())	{		GetNextChar ();	}			if (!IsEOF ())		fCurWord += fCurChar;	 	while (!IsWhiteSpace (GetNextChar ()) && !IsEOF ())	{		fCurWord += fCurChar;	}		return (fCurWord);}char TReadFile::GetNextChar (void){	if (!fIStream.get (fCurChar))		SetFlag (kEOF, true);		return (fCurChar);}void TReadFile::SetToStart (void){	fIStream.seekg (0, ios_base::beg);	SetFlag (kEOF, false);}bool TReadFile::SetToChar (const char character){	while (fCurChar != character && !IsEOF ())	{		GetNextChar ();	}	return (fCurChar == character);}bool TReadFile::SetToWord (const TString& word){	CHECK_ (!word.IsNull ());		while (fCurWord != word && !IsEOF ())	{		GetNextWord ();	}	return (fCurWord == word);}TReadFile::TReadFile (istream& i, TString fileName)	:	fIStream (i)	,	fFileName (fileName)	,	fWhiteSpace (" \t\n\r")	,	fStreamPos (0){	SetFlag (kEOF, false);}TReadFile::~TReadFile (void){}#if PRAGMA_MARK_SUPPORTED#pragma mark -#endif