#include "TCalc.h"#include <math.h>#include "TChecks.h"#include "TString.h"bool IsEven (unsigned int u){		const unsigned int d = (u / 2);	return ((bool)(u == (d * 2)));}int	GetUnits (unsigned int u, unsigned int dps){	CHECK_ (dps < 6);	TString string;	string.AddToThis (u);	if (dps <= string.Length ())	{		switch (string.IthChar (string.Length () - (dps - 1)))		{			case '0':				return (0);							case '1':				return (1);			case '2':				return (2);			case '3':				return (3);			case '4':				return (4);			case '5':				return (5);			case '6':				return (6);			case '7':				return (7);			case '8':				return (8);			case '9':				return (9);			default:				CHECK_ (0);				break;			}	}	return (-1);}